CREATE TABLE IF NOT EXISTS email_config (id uuid not null, created_by varchar(255), created_date int8, last_modified_by varchar(255), last_modified_date int8, connection_timeout int4 not null, email_encryption_method varchar(255), host varchar(255), is_auth_required boolean not null, is_default boolean not null, password varchar(255), port int4 not null, timeout int4 not null, username varchar(255), write_timeout int4 not null, primary key (id))
CREATE TABLE IF NOT EXISTS email_template (id uuid not null, created_by varchar(255), created_date int8, last_modified_by varchar(255), last_modified_date int8, is_default boolean not null, template_body text, template_description varchar(255), template_engine_type int4, template_name varchar(255), primary key (id))
CREATE TABLE IF NOT EXISTS webhook_config (id uuid not null, created_by varchar(255), created_date int8, last_modified_by varchar(255), last_modified_date int8, action_type varchar(255), content_type varchar(255), custom_headers jsonb, is_default boolean not null, payload varchar(255), template_engine_type int4, url varchar(255), primary key (id))
